BEGIN TRANSACTION;
CREATE TABLE diagnoses (
	id INTEGER NOT NULL,
	text VARCHAR(10000) NOT NULL,
	parent_id INTEGER,
	time_created VARCHAR(64) DEFAULT (CURRENT_TIMESTAMP) NOT NULL,
	time_updated VARCHAR(64) DEFAULT (CURRENT_TIMESTAMP) NOT NULL,
	PRIMARY KEY (id),
	FOREIGN KEY(parent_id) REFERENCES diagnoses (id)
);
COMMIT;
